mapper.update:
UPDATE sys_user
SET login_date = GETDATE()
WHERE user_id = #{id}

mapper.select:
SELECT user_name AS username
FROM sys_user
WHERE login_name = #{login_name}

mapper.update:
UPDATE sys_user
SET del_flag = 1
WHERE user_id = #{user_id}

UPDATE sys_user
SET
user_name = #{username},
email = #{email},
role = #{role},
user_type = #{userType}

    , password = #{password}

WHERE login_name = #{login_name}
AND del_flag = 0
